package com.abc.practice2;

public class Nested_if {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10,b=20,c=5;
		if(a<b)
			if(a<c)
				System.out.println("a is minimum");
		else
			if(b>a)
			    if(b>c)
				    System.out.println("b is maximum");
		

	}

}
